INSERT INTO "fortune_fortune" VALUES(2971,'fortunes',126,'Everybody is given the same amount of hormones, at birth, and
if you want to use yours for growing hair, that''s fine with me.');
INSERT INTO "fortune_fortune" VALUES(2972,'fortunes',61,'Everybody is somebody else''s weirdo.
