INSERT INTO "fortune_fortune" VALUES(2975,'fortunes',70,'Everybody needs a little love sometime;
stop hacking and fall in love!');
INSERT INTO "fortune_fortune" VALUES(2976,'fortunes',57,'Everybody wants to go to heaven, but nobody wants to die.');
INSERT INTO "fortune_fortune" VALUES(2977,'fortunes',124,'Everyone can be taught to sculpt: Michelangelo would have had
