INSERT INTO "fortune_fortune" VALUES(2978,'fortunes',57,'Everyone complains of his memory, no one of his judgment.');
INSERT INTO "fortune_fortune" VALUES(2979,'fortunes',40,'Everyone hates me because I''m paranoid.');
INSERT INTO "fortune_fortune" VALUES(2980,'fortunes',80,'Everyone is a genius.  It''s just that some people are too stupid to
