INSERT INTO "fortune_fortune" VALUES(5635,'fortunes',94,'It is often easier to ask for forgiveness than to ask for permission.
		-- Grace Murray Hopper');
INSERT INTO "fortune_fortune" VALUES(5636,'fortunes',81,'It is one thing to praise discipline, and another to submit to it.
		-- Cervantes');
INSERT INTO "fortune_fortune" VALUES(5637,'fortunes',213,'It is only by risking our persons from one hour to another that we live
