INSERT INTO "fortune_fortune" VALUES(14061,'murphy',53,'Whatever hits the fan will not be evenly distributed.');
INSERT INTO "fortune_fortune" VALUES(14062,'murphy',52,'Whatever it is, somebody will have had it for lunch.');
INSERT INTO "fortune_fortune" VALUES(14063,'murphy',82,'When a broken appliance is demonstrated for the repairman,
