INSERT INTO "fortune_fortune" VALUES(14097,'murphy',57,'When you dial a wrong number, you never get a busy signal');
INSERT INTO "fortune_fortune" VALUES(14098,'murphy',54,'When you do not know what you are doing, do it neatly.');
INSERT INTO "fortune_fortune" VALUES(14099,'murphy',95,'When you need to knock on wood is when you realize the
