INSERT INTO "fortune_fortune" VALUES(14104,'murphy',53,'Where you stand on an issue depends on where you sit.');
INSERT INTO "fortune_fortune" VALUES(14105,'murphy',60,'Why worry about tomorrow?  We may not make it through today!');
