INSERT INTO "fortune_fortune" VALUES(14108,'murphy',15,'Workers won''t.');
INSERT INTO "fortune_fortune" VALUES(14109,'murphy',25,'Working capital doesn''t.');
INSERT INTO "fortune_fortune" VALUES(14110,'murphy',118,'Writings prepared without understanding must fail in the
