INSERT INTO "fortune_fortune" VALUES(14115,'murphy',32,'You can never do just one thing.');
INSERT INTO "fortune_fortune" VALUES(14116,'murphy',39,'You can observe a lot just by watching.');
INSERT INTO "fortune_fortune" VALUES(14117,'murphy',97,'You can pray hard enough to make water run uphill
