INSERT INTO "fortune_fortune" VALUES(14119,'murphy',37,'You can''t fix it if it ain''t broke.');
INSERT INTO "fortune_fortune" VALUES(14120,'murphy',39,'You can''t guard against the arbitrary.');
INSERT INTO "fortune_fortune" VALUES(14121,'murphy',60,'You can''t tell how deep a puddle is until you step into it.');
INSERT INTO "fortune_fortune" VALUES(14122,'murphy',65,'You don''t have to be crazy to work here
